use strict;
use warnings;

my @files = @ARGV;

print <<"END_SETTING";
#! nqp

# This file automatically generated by $0 in the nqp-rx project.

END_SETTING

foreach my $file (@files) {
    print "# From $file\n\n";
    open(my $fh, "<",  $file) or die $!;
    local $/;
    my $x = <$fh>;
    close $fh;
    print $x;
}

print "\n# vim: set ft=perl6 nomodifiable :\n";
