use strict;
use warnings;

my @files = @ARGV;

print <<"END_SETTING";
#! nqp

# This file automatically generated by $0 in the NQP project.

END_SETTING

my %classnames;
foreach my $file (@files) {
    print "# From $file\n\n";
    open(my $fh, "<",  $file) or die $!;
    local $/;
    my $x = <$fh>;
    close $fh;
    print $x;
}

print "\n1;\n{YOU_ARE_HERE}\n\n# vim: set ft=perl6 nomodifiable :\n";
