use strict;
use warnings;

my @files = @ARGV;

print <<"END_HEAD";
# This file automatically generated by $0

END_HEAD

foreach my $file (@files) {
    print "# From $file\n\n";
    open(my $fh, "<",  $file) or die $!;
    local $/;
    my $x = <$fh>;
    close $fh;
    print $x;
}

print "\n# vim: set ft=perl6 nomodifiable :\n";
